import React, { useState } from 'react';
import axios from 'axios';
import StyledComponents from './styled';

const {
  TotalWrapper,
  OrcamentoItem,
  OrcamentoList,
  OrcamentoWrapper,
  Price,
  Item,
  ItemDetails,
  ItemList,
  ErrorMessage,
  Header,
  Input,
  InputWrapper,
  Button,
  Container,
  ScrollableItemList,
  StyledNumber,
  ItemNome,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
} = StyledComponents;

const App: React.FC = () => {
  const [termo, setTermo] = useState('');
  const [itens, setItens] = useState<any[]>([]);
  const [orcamento, setOrcamento] = useState<any[]>([]);
  const [erro, setErro] = useState<string>('');
  const [modalItem, setModalItem] = useState<any | null>(null);
  const [quantidade, setQuantidade] = useState<string>('');

  const buscarItens = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/itens?termo=${termo}`);
      setItens(res.data);
      setErro('');
    } catch (err) {
      setErro('Erro ao buscar itens');
      console.error('Erro ao buscar itens', err);
    }
  };

  const abrirModal = (item: any) => {
    setModalItem(item);
    setQuantidade('');
  };

  const fecharModal = () => {
    setModalItem(null);
    setQuantidade('');
  };

  const adicionarAoOrcamento = (item: any, quantidade: number) => {
    if (isNaN(quantidade) || quantidade <= 0) {
      alert('Quantidade inválida!');
      return;
    }

    const itemOrcamento = {
      ...item,
      quantidade,
      materialTotal: item.material * quantidade,
      maoDeObraTotal: item.maoDeObra * quantidade,
      total: (item.material + item.maoDeObra) * quantidade,
    };

    setOrcamento([...orcamento, itemOrcamento]);
    fecharModal();
  };

  return (
    <Container>
      <Header>Consulta de Preços - Tabela PINI</Header>

      <InputWrapper>
        <Input
          type="text"
          value={termo}
          onChange={(e) => setTermo(e.target.value)}
          placeholder="Pesquisar por item"
        />
        <Button onClick={buscarItens}>Buscar</Button>
      </InputWrapper>

      {erro && <ErrorMessage>{erro}</ErrorMessage>}

      <div>
        <h2>Resultados da Pesquisa</h2>
        {itens.length === 0 ? (
          <p>Nenhum item encontrado.</p>
        ) : (
          <ScrollableItemList>
            {itens.map((item) => (
              <Item key={item.id}>
                <ItemDetails>
                  <ItemNome>{item.nome}</ItemNome>
                  <Price>QUANTIDADE: {item.unidade}</Price>
                  <Price>Material: <StyledNumber>R$ {(Number(item.material) || 0).toFixed(2)}</StyledNumber></Price>
                  <Price>Mão de Obra: <StyledNumber>R$ {(Number(item.maoDeObra) || 0).toFixed(2)}</StyledNumber></Price>
                  <p>Total: <StyledNumber>R$ {(Number(item.total) || 0).toFixed(2)}</StyledNumber></p>
                </ItemDetails>
                <Button onClick={() => abrirModal(item)}>Adicionar ao Orçamento</Button>
              </Item>
            ))}
          </ScrollableItemList>
        )}
      </div>

      <OrcamentoWrapper>
        <h2>Orçamento</h2>
        <OrcamentoList>
          {orcamento.map((item, index) => (
            <OrcamentoItem key={index}>
              <div>{item.nome} - {item.quantidade} x Material: R$ {(Number(item.material) || 0).toFixed(2)} + Mão de Obra: R$ {(Number(item.maoDeObra) || 0).toFixed(2)}</div>
              <div>R$ {(Number(item.total) || 0).toFixed(2)}</div>
            </OrcamentoItem>
          ))}
        </OrcamentoList>
        <TotalWrapper>
          Total: R$ {orcamento.reduce((total, item) => total + (Number(item.total) || 0), 0).toFixed(2)}
        </TotalWrapper>
      </OrcamentoWrapper>

      {modalItem && (
        <ModalOverlay>
          <ModalContent>
            <ModalHeader>
              <h3>Adicionar ao Orçamento</h3>
              <Button onClick={fecharModal}>X</Button>
            </ModalHeader>
            <ModalBody>
              <p>Item: {modalItem.nome}</p>
              <p>Material: R$ {(Number(modalItem.material) || 0).toFixed(2)}</p>
              <p>Mão de Obra: R$ {(Number(modalItem.maoDeObra) || 0).toFixed(2)}</p>
              <Input
                type="number"
                value={quantidade}
                onChange={(e) => setQuantidade(e.target.value)}
                placeholder="Digite a quantidade"
              />
            </ModalBody>
            <ModalFooter>
              <Button onClick={() => adicionarAoOrcamento(modalItem, parseFloat(quantidade))}>Adicionar</Button>
            </ModalFooter>
          </ModalContent>
        </ModalOverlay>
      )}
    </Container>
  );
};

export default App;
