import React, { useState } from 'react';
import axios from 'axios';
import StyledComponents from './styled';
import { useNavigate } from 'react-router-dom';  // Use o useNavigate em vez de useHistory

const { Input, Button, ErrorMessage, Container } = StyledComponents;

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const navigate = useNavigate();  // Substitui useHistory por useNavigate

  const BASE_URL = 'http://192.168.15.116:3001';

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${BASE_URL}/api/login`, { email, senha });
      localStorage.setItem('token', res.data.token);  // Armazena o token no localStorage
      navigate('/orcamentos');  // Redireciona para a página de orçamentos após o login bem-sucedido
    } catch (err) {
      setErro('Erro ao fazer login');
      console.error('Erro ao fazer login', err);
    }
  };

  return (
    <Container>
      <h2>Login</h2>
      <Input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
      />
      <Input
        type="password"
        value={senha}
        onChange={(e) => setSenha(e.target.value)}
        placeholder="Senha"
      />
      {erro && <ErrorMessage>{erro}</ErrorMessage>}
      <Button onClick={handleLogin}>Login</Button>
    </Container>
  );
};

export default Login;
