import React, { useState } from 'react';
import axios from 'axios';
import stylined from './index.css'

const App: React.FC = () => {
  const [termo, setTermo] = useState('');
  const [itens, setItens] = useState<any[]>([]);
  const [orcamento, setOrcamento] = useState<any[]>([]);
  const [erro, setErro] = useState<string>('');

  const buscarItens = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/itens?termo=${termo}`);
      setItens(res.data);
      setErro('');
    } catch (err) {
      setErro('Erro ao buscar itens');
      console.error('Erro ao buscar itens', err);
    }
  };

  const adicionarAoOrcamento = (item: any, quantidade: number) => {
    if (isNaN(quantidade) || quantidade <= 0) {
      alert('Quantidade inválida!');
      return;
    }

    const itemOrcamento = {
      ...item,
      quantidade,
      materialTotal: item.material * quantidade,  // Calcula total de material
      maoDeObraTotal: item.maoDeObra * quantidade,  // Calcula total de mão de obra
      total: (item.material + item.maoDeObra) * quantidade,  // Calcula total geral (material + mão de obra)
    };

    setOrcamento([...orcamento, itemOrcamento]);
  };

  return (
    <div>
      <h1>Consulta de Preços - Tabela PINI</h1>
      <input
        type="text"
        value={termo}
        onChange={(e) => setTermo(e.target.value)}
        placeholder="Pesquisar por item"
      />
      <button onClick={buscarItens}>Buscar</button>
  
      {erro && <p style={{ color: 'red' }}>{erro}</p>}
  
      <div>
        <h2>Resultados da Pesquisa</h2>
        {itens.length === 0 ? (
          <p>Nenhum item encontrado.</p>
        ) : (
          <ul>
            {itens.map((item) => (
              <li key={item.id}>
                {item.nome} - Material: {item.material} | Mão de Obra: {item.maoDeObra} | Total: {item.total} {item.unidade}
                <button
                  onClick={() => {
                    const quantidade = prompt('Digite a quantidade');
                    if (quantidade) adicionarAoOrcamento(item, parseFloat(quantidade));
                  }}
                >
                  Adicionar ao Orçamento
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
  
      <div>
        <h2>Orçamento</h2>
        <ul>
          {orcamento.map((item, index) => (
            <li key={index}>
              {item.nome} - {item.quantidade} x Material: {item.material} + Mão de Obra: {item.maoDeObra} = {item.total}
            </li>
          ))}
        </ul>
        <div>
          Total: {orcamento.reduce((total, item) => total + item.total, 0)}
        </div>
      </div>
    </div>
  );
}

export default App;
